import './App.css';
import React, { useState } from 'react'

function App() {
  const [ count, setCount ] = useState(7);

  function decrementCount(){
    setCount(prevCount => {
      count - 1
    })
  }
  return (
    <div className="App">

    <button onClick={ decrementCount }> - </button>
    <span> { count } </span>
    <button> + </button>
    </div>
  );
}

export default App;
